import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function News({ noticias }) {
  const [user, setUser] = useState({
    user: {
      name: "Brais",
      email: "braisfdezlopez@gmail.com",
      bio: null,
      avatar: "1-lvDActk2od.png",
      created_at: "2021-05-21T12:37:04.000Z",
    },
  });

  useEffect(() => {
    const cargarUser = async () => {
      try {
        const respuesta = await fetch(
          `http://localhost:3000/api/v1/users/${noticias.id_user}/profile`
        );
        console.log("respuest", respuesta);
        if (respuesta.ok) {
          const body = await respuesta.json();

          setUser(body);
        }
      } catch (msg) {
        console.error(msg);
      }
    };

    cargarUser();
    const newsCategory = async () => {
      try {
        const res = await fetch(
          `http://localhost:3000/api/v1/news/category/${noticias.category}`
        );
        console.log("respuest", res);
        if (res.ok) {
          const body = await res.json();

          setUser(body);
        }
      } catch (error) {
        console.error(error);
      }
    };

    newsCategory();
  }, [noticias.id_user]);

  return (
    <div className="news">
      <Link to={`/news/${noticias.id}`}>
        <h1>{noticias.news_title}</h1>
      </Link>
      <h2>{noticias.news_lead}</h2>
      <div>
        <h5>Publicado por {user.user.name}</h5>
        <h5>{noticias.created_at}</h5>
      </div>
      <div className="imagenes">
        <img className="icono" src={"/icons/torch.png"} alt="icono"></img>
        <img
          className="imagen"
          src={noticias.picture}
          alt="imagen-noticia"
        ></img>
      </div>
      <p>{noticias.news_text}</p>
    </div>
  );
}

export default News;
