import { useContext, useEffect, useState } from "react";
import { Token } from "../components/token";
import { deleteNews, getProfile } from "../api/api";
import { Link } from "react-router-dom";

function ProfilePage() {
  const [profile, setProfile] = useState(null);
  const [token] = useContext(Token);

  useEffect(() => {
    const cargarProfile = async () => {
      try {
        const response = await getProfile(token);
        if (response.ok) {
          const body = await response.json();
          setProfile(body);
        } else {
          throw new Error("TODO: usar error que ven do backend.");
        }
      } catch (msg) {
        console.error(msg);
      }
    };
    const intervalo = setInterval(cargarProfile, 1000);
    cargarProfile();
    return () => {
      clearInterval(intervalo);
    };
  }, [token]);

  // ELIMINAR NOTICIA

  async function deleteNewsHandler(e) {
    console.log(e);
    const idNews = e.target.value;
    const res = await deleteNews(idNews, token);
    const bodyDeLaRespuesta = await res.json();
    // alert(bodyDeLaRespuesta.error);
    console.log(bodyDeLaRespuesta);
  }
  if (!profile) return <p>Cargando...</p>;

  const fechaAltaUsuario = profile.user.created_at;
  const fechaFormateada = new Date(fechaAltaUsuario).toLocaleString();

  return (
    <div className="profile">
      <img
        className="avatar"
        src={`${process.env.REACT_APP_STATIC}/images/profiles/${profile.user.avatar}`}
        alt="avatar"
      />
      <h1>{profile.user.name}</h1>
      <h3>usuario desde: {fechaFormateada}</h3>
      <button>
        <Link to="/UpdateUserProfile">Editar Perfil</Link>
      </button>
      <ul>
        {profile.news.map((news) => (
          <div>
            <li key={news.id}>
              <Link to={`/news/${news.id}`}>{news.news_title}</Link>
            </li>
            <button onClick={deleteNewsHandler} value={news.id}>
              Borrar noticia
            </button>
          </div>
        ))}
      </ul>
      <ul>
        {profile.comments.map((comment) => (
          <li key={comment.id}>{comment.comment}</li>
        ))}
      </ul>
    </div>
  );
}

export default ProfilePage;
