import { useContext, useState } from "react";
import { Redirect } from "react-router";
import { postLogin } from "../api/api";
import { Token } from "../components/token";
import "./login.css";

const Login = (props) => {
  const [userName, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useContext(Token);
  const [errorMsg, setErrorMsg] = useState(null);
  const handleLogin = async (e) => {
    e.preventDefault();
    const requestBody = {
      userName,
      password,
    };
    const res = await postLogin(requestBody);
    const bodyRespuestaFormulario = await res.json();
    console.log(bodyRespuestaFormulario);
    if (res.ok) {
      setToken(bodyRespuestaFormulario.accessToken);
      setErrorMsg(null);
    } else {
      setToken("");
      setErrorMsg(
        `No se ha podido obtener el token: ${bodyRespuestaFormulario}`
      );
    }
  };
  return (
    <>
      {token ? (
        <Redirect to="/" />
      ) : (
        <div className="login-container">
          <h1 className="title ">Bienvenido a Dpmo</h1>

          <h2>Login</h2>
          <div class="separator"></div>
          <form onSubmit={handleLogin} id="login">
            <div>
              <label htmlFor="email">Email</label>
              <input
                name="email"
                type="email"
                value={userName}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div>
              <label htmlFor="password">Contrase√±a</label>
              <input
                name="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div>
              <input type="submit" value="Enviar"></input>
            </div>
          </form>
          {errorMsg && <div style={{ color: "red" }}>{errorMsg}</div>}
        </div>
      )}
    </>
  );
};

export default Login;
