import { useContext, useState } from "react";
import { Token } from "./token";
import { createNews } from "../api/api";

function PostNews() {
  const [url, setNewsurl] = useState("");
  const [category, setCategory] = useState("");
  const [token] = useContext(Token);

  const handlePostNews = async (e) => {
    console.log(e);
    e.preventDefault();
    const requestBody = {
      url,
      category,
    };
    console.dir(requestBody);
    const res = await createNews(requestBody, token);
    const bodyResponse = await res.json();
    console.log("respuesta", bodyResponse);
  };

  return (
    <form onSubmit={handlePostNews} id="postNews">
      <div>
        <label htmlFor="url">Url de la noticia</label>
        <input
          name="url"
          type="url"
          value={url}
          onChange={(e) => setNewsurl(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="category">Categoria</label>
        <input
          name="category"
          type="text"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        />
      </div>
      <div>
        <input type="submit" value="Enviar"></input>
      </div>
    </form>
  );
}

export default PostNews;
