import { SERVER_URL } from "../portBackend";

function getTopNews() {
  return fetch(`${SERVER_URL}/news/top`);
}
function getDateNews() {
  return fetch(`${SERVER_URL}/news/`);
}

const postRegister = (requestBody) => {
  return fetch(`${SERVER_URL}/users/register`, {
    method: "POST",
    headers: {
      "Content-type": "application/json",
    },
    body: JSON.stringify(requestBody),
  });
};

const postLogin = (requestBody) => {
  return fetch(`${SERVER_URL}/login`, {
    method: "POST",
    headers: {
      "Content-type": "application/json",
    },
    body: JSON.stringify(requestBody),
  });
};

export { getTopNews, postRegister, getDateNews, postLogin };
