import { useState } from "react";
import { postRegister } from "../api/api";
import "./register.css";

function Register() {
  const [errorMsg, setErrorMsg] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [repeatPassword, setRepeatPassword] = useState("");
  const handleRegister = async (e, event) => {
    e.preventDefault();

    const requestBody = {
      name,
      email,
      password,
      repeatPassword,
    };
    console.dir(requestBody);

    const res = await postRegister(requestBody);
    const bodyDeLaRespuesta = await res.json();
    if (res.ok) {
      setErrorMsg(null);
    } else {
      setErrorMsg(`Error: ${bodyDeLaRespuesta}`);
    }
  };
  return (
    <section class="main">
      <div className="register-container">
        <h1 className="title ">Registro</h1>
        <form onSubmit={handleRegister} id="register">
          <div className="container-input">
            <label htmlFor="name">Nombre</label>
            <input
              className="input-register"
              name="name"
              type="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="container-input">
            <label htmlFor="email">Email</label>
            <input
              className="input-register"
              name="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="container-input">
            <label htmlFor="password">Contraseña</label>
            <input
              className="input-register"
              name="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="container-input">
            <label htmlFor="repeatPassword">Contraseña</label>
            <input
              className="input-register"
              name="repeatPassword"
              type="password"
              value={repeatPassword}
              onChange={(e) => setRepeatPassword(e.target.value)}
            />
          </div>
          <div>
            <input className="submit" type="submit" value="Enviar"></input>
          </div>
        </form>
        {errorMsg && <div style={{ color: "red" }}>{errorMsg}</div>}
      </div>
    </section>
  );
}

export default Register;
