import { useContext, useState } from "react";
import { Token } from "./token";
import { createNews } from "../api/api";
import { ErrorContext } from "./error";
import "./postNews.css";
function PostNews() {
  const [url, setNewsurl] = useState("");
  const [category, setCategory] = useState("");
  const [token] = useContext(Token);
  const [, setError] = useContext(ErrorContext);

  const handlePostNews = async (e) => {
    console.log(e);
    e.preventDefault();
    const requestBody = {
      url,
      category,
    };
    try {
      const response = await createNews(requestBody, token);
      const bodyResponse = await response.json();
      if (response.ok) {
        setNewsurl("");
      } else {
        throw new Error(bodyResponse.error);
      }
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="post-container">
      <form className="form-post-news" onSubmit={handlePostNews} id="postNews">
        <div className="contain-title">
          <h1 className="title">Publica Tu noticia.</h1>
          <div className="separator"></div>
          <h2 className="sub-title">
            Podras Publicar tu noticia simplemente copiando la Url de la Noticia
            de la pagina que quieras ,tendras que elegir su categoria entre
            varias opciones , y luego a disfrutar de DPMO. Esperamos que tu
            estadia en Nuestra APP sea de tu agrado. Enjoy!!!!
          </h2>
        </div>
        <div className="div-post-news">
          <label className="label-url" htmlFor="url">
            Url
          </label>
          <input
            className="input-url"
            name="url"
            type="url"
            value={url}
            placeholder="Url de la noticia"
            onChange={(e) => setNewsurl(e.target.value)}
          />
        </div>
        <div className="div-post-news">
          <label className="label-category" htmlFor="category">
            Categoria
          </label>
          <select
            className="select-post-news"
            name="category"
            type="text"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option>ciencia y tecnologia</option>
            <option>cultura</option>
            <option>Deportes</option>
            <option>economia</option>
            <option>Politica</option>
          </select>
        </div>
        <div className="div-post-news">
          <input className="submit" type="submit" value="Enviar"></input>
        </div>
      </form>
    </div>
  );
}

export default PostNews;
