import { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import { Token } from "../components/token";

import { deleteNews, postComment } from "../api/api";
import { postReactionComment } from "../api/api";
// import { deleteNews } from "../api/api";

import { ErrorContext } from "./error";

import Comment from "./Comment";
import News from "./News";
export function NewsSinglePage() {
  // NOTICIA
  const [news, setNews] = useState([]);

  const { id } = useParams();
  const [, setError] = useContext(ErrorContext);

  useEffect(() => {
    async function getNews() {
      try {
        const response = await fetch(
          `http://localhost:3000/api/v1/news/${id}`,
          {
            method: "GET",
            headers: {
              "Content-type": "application/json",
            },
          }
        );
        const json = await response.json();
        if (response.ok) {
          setNews(json);
        } else {
          throw new Error(json.error);
        }
      } catch (error) {
        setError(error.message);
      }
    }
    getNews();
  }, [id, setError]);

  // COMENTARIOS
  const [comments, setComments] = useState([]);
  useEffect(() => {
    const getComment = async () => {
      const response = await fetch(
        `http://localhost:3000/api/v1/news/${id}/comments`,
        {
          method: "GET",
          headers: {
            "Content-type": "application/json",
          },
        }
      );
      if (response.ok) {
        const json = await response.json();
        setComments(json);
      } else {
        console.error("ERROR");
      }
    };
    // const intervalo = setInterval(getComment, 1000);
    getComment();
    // return () => {
    //   clearInterval(intervalo);
    // };
  }, [id]);

  // Crear comentario
  const [createComment, setCreateComment] = useState([]);
  const [token] = useContext(Token);

  const postCommentHandler = async (e) => {
    e.preventDefault();
    const requestBody = {
      comment: createComment,
    };

    const response = await postComment(requestBody, id, token);

    const body = await response.json();
    console.log(body);
  };

  // ELIMINAR NOTICIA

  async function deleteNewsHandler(e) {
    try {
      const idNews = e.target.value;
      console.log(e);
      const res = await deleteNews(idNews, token);
      const bodyDeLaRespuesta = await res.json();
      if (res.ok) {
        setNews({
          ...news,
          news: news.news.filter((n) => n.id !== Number(idNews)),
        });
      } else {
        throw new Error(bodyDeLaRespuesta.error);
      }
    } catch (error) {
      console.error(error);
    }
  }

  async function reactComment(id, reaction) {
    const vote = {
      like: reaction,
      dislike: !reaction,
    };

    const res = await postReactionComment(vote, id, token);

    const data = await res.json();

    console.log(data);

    //TODO: meter esto nun trycatch

    setComments(
      comments.map((comment) => {
        if (comment.id === id) {
          return data;
        }
        return comment;
      })
    );

    // alert(bodyDeLaRespuesta.error);
  }

  console.log(comments);

  return (
    <>
      <News noticias={news}></News>
      <div>
        <button value={news} onClick={deleteNewsHandler}>
          Borrar noticia
        </button>
      </div>
      <div>
        {comments.map((comment) => (
          <Comment
            key={comment.id}
            comment={comment}
            reactComment={reactComment}
          ></Comment>
        ))}
      </div>
      <div>
        <form onSubmit={postCommentHandler}>
          <div>
            <h4>Comentario</h4>
            <textarea
              name="comment"
              type="text"
              rows="8"
              value={createComment}
              onChange={(e) => setCreateComment(e.target.value)}
            />
          </div>
          <input type="submit" value="Publicar Comentario"></input>
        </form>
      </div>
    </>
  );
}
