import { useContext, useState } from "react";
import { Redirect } from "react-router";
import { postLogin } from "../api/api";
import { Token } from "../components/token";

const Login = (props) => {
  const [userName, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useContext(Token);
  const [errorMsg, setErrorMsg] = useState(null);
  const handleLogin = async (e) => {
    e.preventDefault();
    const requestBody = {
      userName,
      password,
    };
    const res = await postLogin(requestBody);
    const bodyRespuestaFormulario = await res.json();
    console.log(bodyRespuestaFormulario);
    if (res.ok) {
      setToken(bodyRespuestaFormulario.accessToken);
      setErrorMsg(null);
    } else {
      setToken("");
      setErrorMsg(
        `No se ha podido obtener el token: ${bodyRespuestaFormulario}`
      );
    }
  };
  return (
    <>
      {token ? (
        <Redirect to="/" />
      ) : (
        <div>
          <h2>Login</h2>
          <form className="row" onSubmit={handleLogin} id="login">
            <div className="col-md-3">
              <label htmlFor="email">Email</label>
              <input
                name="email"
                type="email"
                value={userName}
                placeholder=" Ingrese su Password"
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="col-md-3">
              <label htmlFor="password">Contrase√±a</label>
              <input
                name="password"
                type="password"
                value={password}
                placeholder=" Ingrese su Password"
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="col-md-3">
              <label htmlFor="button">
                <button
                  className="btn btn-primary"
                  type="submit"
                  value="Enviar"
                >
                  Enviar
                </button>
              </label>
            </div>
          </form>
          {errorMsg && <div style={{ color: "red" }}>{errorMsg}</div>}
        </div>
      )}
    </>
  );
};

export default Login;
