import "./fire.css";

function Fire({ counterTop }) {
  let clase;
  if (counterTop < 6) {
    clase = "flames-peq";
  } else if (counterTop < 11) {
    clase = "flames-med";
  } else {
    clase = "flames";
  }

  return (
    <div className="fire">
      <div className={clase}>
        <div className="flame"></div>
        <div className="flame"></div>
        <div className="flame"></div>
        <div className="flame"></div>
      </div>
      <div className="logs"></div>
    </div>
  );
}
export default Fire;
