import { useContext, useState } from "react";
import { updateProfile } from "../api/api";
import { Token } from "../components/token";

const UpdateUserProfile = (props) => {
  const [name, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [repeatPassword, setRepeatPassword] = useState("");
  const [email, setEmail] = useState("");
  const [bio, setBiography] = useState("");
  const [token] = useContext(Token);

  const handleEditUserProfile = async (e) => {
    e.preventDefault();
    const requestBody = {
      name,
      email,
      password,
      repeatPassword,
      bio,
    };

    const response = await updateProfile(requestBody, token);

    const body = await response.json();
    console.log(body);
  };

  return (
    <div>
      <h2>Update Profile</h2>
      <form onSubmit={handleEditUserProfile}>
        <div>
          <label htmlFor="name">nombre</label>
          <input
            name="name"
            type="name"
            value={name}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="email">Repetir Contraseña</label>
          <input
            name="email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="password">Contraseña</label>
          <input
            name="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="password">Repetir Contraseña</label>
          <input
            name="repeatPassword"
            type="password"
            value={repeatPassword}
            onChange={(e) => setRepeatPassword(e.target.value)}
          />
        </div>
        <div>
          <h4>Tu biografía</h4>
          <textarea
            name="bio"
            type="text"
            rows="8"
            value={bio}
            onChange={(e) => setBiography(e.target.value)}
          />
        </div>
        <input type="submit" value="Guardar Cambios"></input>
      </form>
    </div>
  );
};

export default UpdateUserProfile;
