import { useContext, useRef, useState } from "react";
import { Token } from "../components/token";
import Footer from "./footer";
import { createPArtnerNews } from "../api/api";
import { ErrorContext } from "./error";

const PartnerPostNews = () => {
  const [title] = useState("");
  const [lead] = useState("");
  const [text] = useState("");
  const [token] = useContext(Token);
  const [category, setCategory] = useState("");
  const fileInput = useRef();

  const [, setError] = useContext(ErrorContext);

  const handlePartnerNews = async (e) => {
    // console.log(e);
    e.preventDefault();
    const fileFromRef = fileInput.current.text;
    let requestBody = new FormData(title, lead, text, category);
    requestBody.append(fileFromRef);
    console.log(requestBody);
    try {
      const response = await createPArtnerNews(requestBody, token);
      //   console.log(token);
      const data = await response.json();
      console.log(data);
      if (response.ok) {
        console.log(data);
      } else {
        console.log(data);
      }
    } catch (error) {
      setError(error.message);
    }
  };
  return (
    <>
      <main className="main-container">
        <form
          className="form-post-news"
          onSubmit={handlePartnerNews}
          id="PartnerPostNews"
        >
          <div className="container-title-partner">
            <h1 className="title-partener">Crear Noticia Partner.</h1>
            <div className="separator-post-partner"></div>
            <h2 className="sub-title-partner">
              Bienvenido DPMO ,Como usuario partener que eres ,puedes crear tu
              propia noticia .
            </h2>
          </div>

          <div className="div-post-partner">
            {/* <i class="fas fa-link"></i> */}
            <label className="title-noticia" htmlFor="text"></label>
            <input
              className="input-text-title"
              name="title"
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Titulo de laNoticia"
            />
          </div>
          <div className="div-post-partner">
            {/* <i class="fas fa-link"></i> */}
            <label className="lead-noticia" htmlFor="text"></label>
            <input
              className="input-text-lead"
              name="lead"
              type="text"
              value={lead}
              onChange={(e) => setLead(e.target.value)}
              placeholder="Subtitulo de la Noticia"
            />
          </div>
          <div className="div-post-partner">
            {/* <i class="fas fa-link"></i> */}
            <label className="text-noticia" htmlFor="text"></label>
            <textarea
              className="input-text-partner"
              name="text"
              type="text"
              rows="8"
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Escribe aqui el Contenido de tu Noticia"
            />
          </div>

          <div className="div-post-news">
            {/* <i class="fas fa-archive"></i> */}
            <label className="label-category" htmlFor="category"></label>
            <select
              className="select-post-news"
              name="category"
              type="text"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
            >
              <option>ciencia y tecnologia</option>
              <option>cultura</option>
              <option>Deportes</option>
              <option>economia</option>
              <option>Politica</option>
            </select>
          </div>
          <div className="div-post-submit">
            <input className="submit-post" type="submit" value="Enviar"></input>
          </div>
        </form>
      </main>
      <Footer />
    </>
  );
};

export default PartnerPostNews;
