import { useEffect, useState } from "react";

function Comment({ comment }) {
  const [user, setUser] = useState({
    user: {
      name: "Brais",
      email: "braisfdezlopez@gmail.com",
      bio: null,
      avatar: "1-lvDActk2od.png",
      created_at: "2021-05-21T12:37:04.000Z",
    },
  });

  useEffect(() => {
    const cargarUser = async () => {
      try {
        const respuesta = await fetch(
          `http://localhost:3000/api/v1/users/${comment.id_user}/profile`
        );
        console.log("respuest", respuesta);
        if (respuesta.ok) {
          const body = await respuesta.json();

          setUser(body);
        }
      } catch (msg) {
        console.error(msg);
      }
    };

    cargarUser();
  }, [comment.id_user]);
  return (
    <div className="comment">
      <div>
        <h5>Publicado por {user.user.name}</h5>
        <h5>{comment.created_at}</h5>
      </div>
      <p>{comment.comment}</p>
    </div>
  );
}

export default Comment;
