import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Comment from "./Comment";
import News from "./News";

export function NewsSinglePage() {
  // NOTICIA
  const [news, setNews] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    async function getNews() {
      const response = await fetch(`http://localhost:3000/api/v1/news/${id}`, {
        method: "GET",
        headers: {
          "Content-type": "application/json",
        },
      });
      if (response.ok) {
        const json = await response.json();
        setNews(json);
      } else {
        console.error("ERROR");
      }
    }
    getNews();
  }, [id]);

  // COMENTARIOS
  const [comments, setComments] = useState([]);
  useEffect(() => {
    const getComment = async () => {
      const response = await fetch(
        `http://localhost:3000/api/v1/news/${id}/comments`,
        {
          method: "GET",
          headers: {
            "Content-type": "application/json",
          },
        }
      );
      if (response.ok) {
        const json = await response.json();
        setComments(json);
      } else {
        console.error("ERROR");
      }
    };
    const intervalo = setInterval(getComment, 1000);
    getComment();
    return () => {
      clearInterval(intervalo);
    };
  }, [id]);

  return (
    <>
      <News noticias={news}></News>
      <div>
        {comments.map((comment) => (
          <Comment key={comment.id} comment={comment}></Comment>
        ))}
      </div>
    </>
  );
}
