import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import News from "./News";

function NewsCategory() {
  // noticias  categorias
  const [categoriasPintado, setCategory] = useState([]);
  const { category } = useParams();

  useEffect(() => {
    async function getCategory() {
      const response = await fetch(
        `http://localhost:3000/api/v1/news/category/${category}`,
        {
          method: "GET",
          headers: {
            "Content-type": "application/json",
          },
        }
      );

      if (response.ok) {
        const json = await response.json();
        console.log("json", json);
        setCategory(json);
      } else {
        console.error("ERROR");
      }
    }
    getCategory();
  }, [category]);

  return (
    <div>
      {categoriasPintado.map((news) => (
        <Link
          to={`http://localhost:3000/api/v1/news/category/${news.category}`}
        >
          <News key={news.id} noticias={news}></News>
        </Link>
      ))}
    </div>
  );
}

export default NewsCategory;
