import { useEffect, useState } from "react";
import { getTopNews, getDateNews } from "../api/api";

function useRemoteNewsTop() {
  const [topNews, setTopNews] = useState([]);
  useEffect(() => {
    const cargarTopNews = async () => {
      try {
        const respuesta = await getTopNews();
        if (respuesta.ok) {
          const body = await respuesta.json();
          setTopNews(body);
        } else {
          setTopNews([]);
        }
      } catch (msg) {
        console.error(msg);
      }
    };
    // const intervalo = setInterval(cargarTopNews, 1000);
    cargarTopNews();
    // return () => {
    //   clearInterval(intervalo);
    // };
  }, []);

  return [topNews, setTopNews];
}

function useRemoteNewsDate() {
  const [dateNews, setDateNews] = useState([]);

  useEffect(() => {
    const cargarDateNews = async () => {
      try {
        const respuesta = await getDateNews();
        if (respuesta.ok) {
          const body = await respuesta.json();

          setDateNews(body);
        } else {
          setDateNews([]);
        }
      } catch (msg) {
        console.error(msg);
      }
    };
    // const intervalo = setInterval(cargarDateNews, 1000);
    cargarDateNews();
    // return () => {
    //   clearInterval(intervalo);
    // };
  }, []);

  return [dateNews, setDateNews];
}

export { useRemoteNewsTop, useRemoteNewsDate };
