import { useContext, useEffect, useRef, useState } from "react";
import { updateProfile, postFileImage, getProfile } from "../api/api";
import { Token } from "../components/token";
import { ErrorContext } from "./error";
import Footer from "./footer";

const UpdateUserProfile = () => {
  const [name, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [repeatPassword, setRepeatPassword] = useState("");
  const [email, setEmail] = useState("");
  const [bio, setBiography] = useState("");
  const [token] = useContext(Token);
  const [, setFile] = useState();
  const [, setError] = useContext(ErrorContext);
  const fileInput = useRef();

  useEffect(() => {
    const loadProfile = async () => {
      try {
        const response = await getProfile(token);
        const body = await response.json();
        if (response.ok) {
          // setProfile()
          setUsername(body.user.name);
          setPassword(body.user.password);
          setRepeatPassword(body.user.password);
          setEmail(body.user.email);
          setBiography(body.user.bio);
        } else {
          throw new Error(body.error);
        }
      } catch (error) {
        setError(error.message);
      }
    };
    loadProfile();
  }, [token, setError]);

  const updateAvatar = async (e) => {
    e.preventDefault();
    const fileFromRef = fileInput.current.files[0];
    let requestBody = new FormData();
    requestBody.append("profileImage", fileFromRef);
    try {
      const res = await postFileImage(requestBody, token);
      const data = await res.json();
      if (res.ok) {
      } else {
        throw new Error(data.error);
      }
    } catch (error) {
      setError(error.message);
    }
  };
  const saveAvatar = (e) => {
    console.dir(e.target);
    setFile(e.target.files[0]);
  };

  const handleEditUserProfile = async (e) => {
    e.preventDefault();
    const requestBody = {
      name,
      email,
      password,
      repeatPassword,
      bio,
    };

    const response = await updateProfile(requestBody, token);

    const body = await response.json();
    console.log(body);
  };

  return (
    <>
      <div>
        <form onSubmit={updateAvatar}>
          <div>
            <input type="file" ref={fileInput} onChange={saveAvatar}></input>
            <input type="submit" value="Guardar"></input>
          </div>
        </form>

        <h2>Update Profile</h2>
        <form onSubmit={handleEditUserProfile}>
          <div>
            <label htmlFor="name">nombre</label>
            <input
              name="name"
              type="name"
              value={name}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="email">email</label>
            <input
              name="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="password">Contraseña</label>
            <input
              name="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="password">Repetir Contraseña</label>
            <input
              name="repeatPassword"
              type="password"
              value={repeatPassword}
              onChange={(e) => setRepeatPassword(e.target.value)}
            />
          </div>
          <div>
            <h4>Tu biografía</h4>
            <textarea
              name="bio"
              type="text"
              rows="8"
              value={bio}
              onChange={(e) => setBiography(e.target.value)}
            />
          </div>
          <input type="submit" value="Guardar Cambios"></input>
        </form>
      </div>
      <Footer />
    </>
  );
};

export default UpdateUserProfile;
