import { useContext } from "react";
import { Link } from "react-router-dom";
import { Token } from "../components/token";
import { ErrorContext } from "./error";

function News({ noticias }) {
  const [token] = useContext(Token);
  const [, setError] = useContext(ErrorContext);

  async function upVoteHandler() {
    const vote = {
      mas: true,
      menos: false,
    };
    try {
      const response = await fetch(
        `http://localhost:3000/api/v1/news/${noticias.id}/vote`,
        {
          method: "PUT",
          headers: {
            "Content-type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify(vote),
        }
      );
      const bodyDeLaRespuesta = await response.json();
      if (response.ok) {
      } else {
        throw new Error(bodyDeLaRespuesta.error);
      }
    } catch (error) {
      setError(error.message);
    }
  }

  async function downVoteHandler() {
    const vote = {
      mas: false,
      menos: true,
    };
    console.log(noticias.id);
    const res = await fetch(
      `http://localhost:3000/api/v1/news/${noticias.id}/vote`,
      {
        method: "PUT",
        headers: {
          "Content-type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(vote),
      }
    );
    const bodyDeLaRespuesta = await res.json();
    alert(bodyDeLaRespuesta.error);
  }

  const fechaCreacion = noticias.created_at;
  const fechaFormateada = new Date(fechaCreacion).toLocaleString();

  // console.log(
  //   fecha.getDay(),
  //   fecha.getMonth() + 1,
  //   fecha.getFullYear(),
  //   fecha.getHours(),
  //   fecha.getMinutes(),
  //   fecha.getSeconds()
  // );

  return (
    <div className="news">
      <Link to={`/news/${noticias.id}`}>
        <h1>{noticias.news_title}</h1>
      </Link>
      <h2>{noticias.news_lead}</h2>
      <div>
        <h5>
          Publicado por {noticias.user_name} el {fechaFormateada}
        </h5>
      </div>
      <div className="imagenes">
        <img className="icono" src={"/icons/torch.png"} alt="icono"></img>
        <button onClick={upVoteHandler}>➕</button>
        <div>{noticias.counter_news}</div>
        <button onClick={downVoteHandler}>➖</button>
        <img
          className="imagen"
          src={noticias.picture}
          alt="imagen-noticia"
        ></img>
      </div>
      <p>{noticias.news_text}</p>
    </div>
  );
}

export default News;
