import { useContext, useEffect, useState } from "react";
import { Redirect, useParams } from "react-router-dom";
import { Token } from "../components/token";
import { deleteComment, deleteNews, postComment } from "../api/api";
import { postReactionComment, getSingleNewsComments } from "../api/api";
import Footer from "./footer";
import { ErrorContext } from "./error";
import Comment from "./Comment";
import News from "./News";
import jwt_decode from "jwt-decode";

export function NewsSinglePage() {
  // cargar noticias
  const [news, setNews] = useState([]);
  const { id } = useParams();
  const [, setError] = useContext(ErrorContext);
  const [token] = useContext(Token);

  const decodedToken = jwt_decode(token);
  const { id: idToken } = decodedToken;

  console.log(idToken);

  useEffect(() => {
    async function getNews() {
      try {
        const response = await fetch(
          `http://localhost:3000/api/v1/news/${id}`,
          {
            method: "GET",
            headers: {
              "Content-type": "application/json",
            },
          }
        );
        const json = await response.json();
        if (response.ok) {
          setNews(json);
        } else {
          throw new Error(json.error);
        }
      } catch (error) {
        setError(error.message);
      }
    }
    getNews();
  }, [id, setError]);

  // Cargar comentarios

  const [comments, setComments] = useState([]);
  console.log(comments);
  useEffect(() => {
    const getComments = async () => {
      try {
        const response = await getSingleNewsComments(id);
        const body = await response.json();
        if (response.ok) {
          setComments(body);
        } else {
          throw new Error(body.error);
        }
      } catch (error) {
        setError(error.message);
      }
    };
    getComments();
  }, [id, setError]);

  // Crear comentario

  const [createComment, setCreateComment] = useState([]);

  const postCommentHandler = async (e) => {
    e.preventDefault();
    const requestBody = {
      comment: createComment,
    };

    try {
      const response = await postComment(requestBody, id, token);
      const body = await response.json();
      if (response.ok) {
        const getComments = async () => {
          try {
            const response = await getSingleNewsComments(id);
            const body = await response.json();
            if (response.ok) {
              setComments(body);
            } else {
              throw new Error(body.error);
            }
          } catch (error) {
            setError(error.message);
          }
        };
        getComments();
      } else {
        throw new Error(body.error);
      }
    } catch (error) {
      setError(error.message);
    }
  };

  // Eliminar noticia

  async function deleteNewsHandler(e) {
    try {
      const idNews = e.target.value;
      const res = await deleteNews(idNews, token);
      const bodyDeLaRespuesta = await res.json();
      if (res.ok) {
        setNews();
      } else {
        throw new Error(bodyDeLaRespuesta.error);
      }
    } catch (error) {
      setError(error.message);
    }
  }

  // Eliminar comentario

  async function deleteCommentHandler(e) {
    try {
      const idComment = e.target.value;
      console.log(idComment);
      const res = await deleteComment(idComment, token);
      const bodyDeLaRespuesta = await res.json();
      if (res.ok) {
        setComments(
          comments.filter((comment) => comment.id !== Number(idComment))
        );
      } else {
        throw new Error(bodyDeLaRespuesta.error);
      }
    } catch (error) {
      setError(error.message);
    }
  }

  //Reaccionar a Comentario

  async function reactComment(id, reaction) {
    const vote = {
      like: reaction,
      dislike: !reaction,
    };
    try {
      const res = await postReactionComment(vote, id, token);
      const data = await res.json();
      if (res.ok) {
        setComments(
          comments.map((comment) => {
            if (comment.id === id) {
              return data;
            }
            return comment;
          })
        );
      } else {
        throw new Error(data.error);
      }
    } catch (error) {
      setError(error.message);
    }
  }

  return (
    <>
      {!news ? (
        <Redirect to="/" />
      ) : (
        <>
          <News noticias={news}></News>
          <div>
            {news.id_user === idToken ? (
              <button
                style={{ display: "block" }}
                value={news.id}
                onClick={deleteNewsHandler}
              >
                Borrar noticia
              </button>
            ) : (
              <button
                style={{ display: "none" }}
                value={news.id}
                onClick={deleteNewsHandler}
              >
                Borrar noticia
              </button>
            )}
          </div>
          <div>
            {comments.map((comment) => (
              <>
                <Comment
                  key={comment.id}
                  comment={comment}
                  reactComment={reactComment}
                ></Comment>
                {comment.id_user === idToken ? (
                  <button
                    style={{ display: "block" }}
                    value={comment.id}
                    onClick={deleteCommentHandler}
                  >
                    Eliminar
                  </button>
                ) : (
                  <button
                    style={{ display: "none" }}
                    value={comment.id}
                    onClick={deleteCommentHandler}
                  >
                    Eliminar
                  </button>
                )}
              </>
            ))}
          </div>
          <div>
            <form onSubmit={postCommentHandler}>
              <div>
                <h4>Comentario</h4>
                <textarea
                  name="comment"
                  type="text"
                  rows="8"
                  value={createComment}
                  onChange={(e) => setCreateComment(e.target.value)}
                />
              </div>
              <input type="submit" value="Publicar Comentario"></input>
            </form>
          </div>
          <Footer />
        </>
      )}
    </>
  );
}
