import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useRemoteNewsDate } from "../hooks/remoteHooks";
import "./carousel.css";

const CarouselDateNews = () => {
  const [dateNews] = useRemoteNewsDate();
  const [currentNews, setCurrentNews] = useState(0);

  const prevSlide = () => {
    const resetCarousel = currentNews === 0;
    const index = resetCarousel ? dateNews.length - 1 : currentNews - 1;

    setCurrentNews(index);
  };

  const nextSlide = () => {
    const resetIndex = currentNews === dateNews.length - 1;
    const index = resetIndex ? 0 : currentNews + 1;

    setCurrentNews(index);
  };

  const lastNews = dateNews.slice(currentNews, currentNews + 4);
  const newsToDisplay =
    lastNews.length < 4
      ? [...lastNews, ...dateNews.slice(0, 4 - lastNews.length)]
      : lastNews;

  // let lastNews;
  // let newsToDisplay;
  // if (window.screen.width < 600) {
  //   lastNews = dateNews.slice(currentNews, currentNews + 1);
  //   newsToDisplay =
  //     lastNews.length < 1
  //       ? [...lastNews, ...dateNews.slice(0, 1 - lastNews.length)]
  //       : lastNews;
  // } else if (window.screen.width > 600) {
  //   lastNews = dateNews.slice(currentNews, currentNews + 2);
  //   newsToDisplay =
  //     lastNews.length < 2
  //       ? [...lastNews, ...dateNews.slice(0, 2 - lastNews.length)]
  //       : lastNews;
  // }

  // let lastNews;
  // let newsToDisplay;
  // if (window.matchMedia("screen and (min-width: 768px)").matches) {
  //   lastNews = dateNews.slice(currentNews, currentNews + 1);
  //   newsToDisplay =
  //     lastNews.length < 1
  //       ? [...lastNews, ...dateNews.slice(0, 1 - lastNews.length)]
  //       : lastNews;
  // } else if (window.matchMedia("screen and (min-width: 908px)").matches) {
  //   lastNews = dateNews.slice(currentNews, currentNews + 2);
  //   newsToDisplay =
  //     lastNews.length < 2
  //       ? [...lastNews, ...dateNews.slice(0, 2 - lastNews.length)]
  //       : lastNews;
  // }

  return (
    <div className="carousel">
      <button onClick={prevSlide}>
        <i className="fas fa-arrow-circle-left"></i>
      </button>
      <ul>
        {newsToDisplay.map((news) => (
          <Link to={`/news/${news.id}`}>
            <li className="container-news" key={news.id}>
              <img
                className="img-carousel"
                src={news.picture}
                alt={news.title}
              />
              <div className="container-title">
                <p>{news.news_title}</p>
              </div>
            </li>
          </Link>
        ))}
      </ul>
      <button onClick={nextSlide}>
        <i className="fas fa-arrow-alt-circle-right"></i>
      </button>
    </div>
  );
};

export default CarouselDateNews;
