import { useContext, useEffect, useState } from "react";
import { Token } from "../components/token";
import { getProfile } from "../api/api";

function ProfilePage() {
  const [profile, setProfile] = useState(null);
  const [token] = useContext(Token);

  useEffect(() => {
    const cargarProfile = async () => {
      try {
        const response = await getProfile(token);
        if (response.ok) {
          const body = await response.json();
          setProfile(body);
        } else {
          throw new Error("TODO: usar error que ven do backend.");
        }
      } catch (msg) {
        console.error(msg);
      }
    };
    cargarProfile();
  }, [token]);

  if (!profile) return <p>Cargando...</p>;

  return (
    <div>
      <h3>{profile.user.name}</h3>
      <h5>{profile.user.email}</h5>
      <img
        src={`${process.env.REACT_APP_STATIC}/images/profiles/${profile.user.avatar}`}
        alt="avatar"
      />
      <ul>
        {profile.news.map((news) => (
          <li key={news.id}>{news.news_title}</li>
        ))}
      </ul>
    </div>
  );
}

export default ProfilePage;
