import { useContext, useRef, useState } from "react";
import { Token } from "../components/token";
import { createPartnerNews } from "../api/api";
import { ErrorContext } from "./error";
import "./partnerPostNews.css";

const PartnerPostNews = () => {
  const fileInput = useRef();
  const titleInput = useRef();
  const leadInput = useRef();
  const textInput = useRef();
  const categoryInput = useRef();
  const [token] = useContext(Token);
  const [, setFile] = useState();
  const [, setTitle] = useState();
  const [, setLead] = useState();
  const [, setText] = useState();
  const [, setCategory] = useState();
  const [, setError] = useContext(ErrorContext);

  const postNews = async (e) => {
    e.preventDefault();
    const fileFromRef = fileInput.current.files[0];
    const titleFromRef = titleInput.current.value;
    const leadFromRef = leadInput.current.value;
    const textFromRed = textInput.current.value;
    const categoryFromRef = categoryInput.current.value;
    let requestBody = new FormData();
    requestBody.append("image", fileFromRef);
    requestBody.append("title", titleFromRef);
    requestBody.append("lead", leadFromRef);
    requestBody.append("text", textFromRed);
    requestBody.append("category", categoryFromRef);
    try {
      const res = await createPartnerNews(requestBody, token);
      console.log(requestBody);
      const data = await res.json();
      if (res.ok) {
      } else {
        throw new Error(data.error);
      }
    } catch (error) {
      setError(error.message);
    }
  };
  const saveImage = (e) => {
    setFile(e.target.files[0]);
  };

  const saveTitle = (e) => {
    setTitle(e.target.value);
  };

  const saveLead = (e) => {
    setLead(e.target.value);
  };

  const saveText = (e) => {
    setText(e.target.value);
  };

  const saveCategory = (e) => {
    setCategory(e.target.value);
  };

  return (
    <main className="main-container">
      <div className="container-title-partner">
        <h1 className="title-partener">Crear Noticia Partner.</h1>
        <div className="separator-post-partner"></div>
        <h2 className="sub-title-partner">
          Bienvenido DPMO ,Como usuario partener que eres ,puedes crear tu
          propia noticia .
        </h2>
      </div>
      <form className="form-post-news" onSubmit={postNews}>
        <div>
          <label className="label-image" htmlFor="image" />
          <input
            className="input-post-image"
            type="file"
            ref={fileInput}
            name="image"
            onChange={saveImage}
          ></input>
        </div>
        <div className="div-post-partner">
          <i class="fas fa-pencil-alt"></i>
          <label className="label-title" htmlFor="title" />
          <input
            className="input-post-partner"
            type="text"
            ref={titleInput}
            name="title"
            placeholder="Título noticia"
            onChange={saveTitle}
          ></input>
        </div>
        <div className="div-post-partner">
          <i class="fas fa-pencil-alt"></i>
          <label className="label-lead" htmlFor="lead" />
          <input
            className="input-post-partner"
            type="text"
            ref={leadInput}
            name="lead"
            placeholder="Subtítulo noticia"
            onChange={saveLead}
          ></input>
        </div>
        <div className="div-post-partner">
          <i class="fas fa-pencil-alt"></i>
          <label className="label-text" htmlFor="text" />
          <textarea
            className="input-post-partner"
            row="8"
            type="text"
            ref={textInput}
            name="text"
            placeholder="Cuerpo noticia"
            onChange={saveText}
          ></textarea>
        </div>
        <div className="div-post-partner-news">
          <i class="fas fa-archive"></i>
          <label className="label-category" htmlFor="category"></label>
          <select
            className="select-post-partner-news"
            name="category"
            type="text"
            ref={categoryInput}
            onChange={saveCategory}
          >
            <option>ciencia y tecnologia</option>
            <option>cultura</option>
            <option>deportes</option>
            <option>economia</option>
            <option>politica</option>
          </select>
        </div>
        <div className="div-post-submit">
          <input className="submit-post" type="submit" value="Publicar"></input>
        </div>
      </form>
    </main>
  );
};

export default PartnerPostNews;
