import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useRemoteNewsTop } from "../hooks/remoteHooks";
import useWindowSize from "../hooks/useWindowSize";
import "./carousel.css";

const CarouselTopNews = () => {
  const [topNews] = useRemoteNewsTop(null);
  const [currentNews, setCurrentNews] = useState(0);
  const [size] = useWindowSize();

  const prevSlide = () => {
    const resetCarousel = currentNews === 0;
    const index = resetCarousel ? topNews.length - 1 : currentNews - 1;

    setCurrentNews(index);
  };

  const nextSlide = () => {
    const resetIndex = currentNews === topNews.length - 1;
    const index = resetIndex ? 0 : currentNews + 1;

    setCurrentNews(index);
  };

  let numberOfNews;
  if (size.width < 650) {
    numberOfNews = 1;
  } else if (size.width > 650 && size.width < 1200) {
    numberOfNews = 2;
  } else if (size.width > 1200 && size.width < 1600) {
    numberOfNews = 3;
  } else if (size.width > 1600) {
    numberOfNews = 4;
  }

  const lastNews = topNews.slice(currentNews, currentNews + numberOfNews);
  const newsToDisplay =
    lastNews.length < numberOfNews
      ? [...lastNews, ...topNews.slice(0, numberOfNews - lastNews.length)]
      : lastNews;

  return (
    <>
      <div>{size.width}px</div>
      <div className="carousel">
        <button onClick={prevSlide}>
          <i className="fas fa-arrow-circle-left"></i>
        </button>
        <ul>
          {newsToDisplay.map((news) => (
            <Link key={news.id} to={`/news/${news.id}`}>
              <li className="container-news">
                <img
                  className="img-carousel"
                  src={
                    news.picture.includes("http")
                      ? news.picture
                      : `${process.env.REACT_APP_STATIC}/images/news/${news.picture}`
                  }
                  alt={news.title}
                />
                <div className="container-title">
                  <p>{news.news_title}</p>
                </div>
              </li>
            </Link>
          ))}
        </ul>
        <button onClick={nextSlide}>
          <i className="fas fa-arrow-alt-circle-right"></i>
        </button>
      </div>
    </>
  );
};

export default CarouselTopNews;
